<?php	function group(){		$content .= '<br/><div class="ade">'.TGRUP.'</div>';			$content .= '<div class="" align="center"><span><a class="btn btn-outline-primary btn-rounded btn-fw" href="'.PATH_GRUP.'">View</a></span> <span><a class="btn btn-outline-primary btn-rounded btn-fw" href="'.PATH_GRUP.ADD.'">Add</a></span></div>';			$content .= '<br/><div id="example1" style="width: 1030px; height: 500px; overflow: hidden; font-size=10px;"></div>';			//-------set lebar kolom -------------			$width = "[150,250,100,100,100,100,130,100,100]";			//-------get id pada sql -------------			$field = gen_mysql_id(GRUP);			//-------get header pada sql----------			$name = gen_mysql_head(GRUP);			//-------set header pada handson------			$sethead = "['Group ID','Group','Insert','View','Delete','Edit','Full Controlled','Modified','Remove']";			//-------set id pada handson----------			$setid = "[{data:'ID_Group',className: 'htLeft'},{data:'Group_Name',className: 'htLeft'},{data:'Insert_Data',className: 'htLeft'},{data:'View_Data',className: 'htLeft'},{data:'Delete_Data',className: 'htLeft'},{data:'Edit_Data',className: 'htLeft'},{data:'Full_Control',className: 'htLeft'},{data:'Edit',renderer: 'html'},{data:'Delete',renderer: 'html'}]";			//-------get data pada sql------------			$dt = array(GRUP.' ORDER BY group_name ASC',$field,array('Edit','Delete'),array(PATH_GRUP.EDIT,PATH_GRUP.DELETE),array(),PATH_GRUP);			$data = get_data_handson_func($dt);			//----Fungsi memanggil data handsontable melalui javascript---			$fixedcolleft=0;			$sethandson = array($sethead,$setid,$data,$width,$fixedcolleft);			//--------fungsi hanya untuk meload data			$content .= get_handson($sethandson); 						//------------Jika ada halaman tambah data-------// 			if(isset($_REQUEST['add'])){				$content = '<br/><div class="ade">'.TAGRUP.'</div>';				$content .= '<div class="" align="center"><span><a class="btn btn-outline-primary btn-rounded btn-fw" href="'.PATH_GRUP.'">View</a></span> <span><a class="btn btn-outline-primary btn-rounded btn-fw" href="'.PATH_GRUP.ADD.'">Add</a></span></div>';				//----- Buat Form Isian Berikut-----				$name_field=array('Group','Insert','View','Delete','Edit','Full Controlled');				$input_type=array(							text_je(array('group','','false')),							combo_je_arr(array(array('Yes','No'),'insert','insert',150,'','Yes')),							combo_je_arr(array(array('Yes','No'),'view','view',150,'','Yes')),							combo_je_arr(array(array('Yes','No'),'deleted','deleted',150,'','Yes')),							combo_je_arr(array(array('Yes','No'),'edited','edited',150,'','Yes')),							combo_je_arr(array(array('Yes','No'),'full','full',150,'','Yes'))						);				$signtofill = array('','','','','','');				$content .= create_form(array(FAGRUP,PATH_GRUP.ADD.POST,1,$name_field,$input_type,$signtofill));				//------ Aksi ketika post menambahkan data -----//				if(isset($_REQUEST['post'])){						//-- Insert data pada kategori aset --//						//-- Generate a new id untuk kategori aset --// 						$grupid=get_new_code(array('GROUP','',1)); 						$field = array(								'id_group', 								'group_name',									'insert_data',								'view_data',								'delete_data',								'edit_data',								'full_control');						$value = array(								'"'.$grupid.'"',								'"'.$_REQUEST['group'].'"',								'"'.$_REQUEST['insert'].'"',								'"'.$_REQUEST['view'].'"',								'"'.$_REQUEST['deleted'].'"',								'"'.$_REQUEST['edited'].'"',								'"'.$_REQUEST['full'].'"'); 						$query = mysql_stat_insert(array('tb_user_group',$field,$value)); 						mysql_exe_query(array($query,1)); 						//-- Ambil data baru dari database --//						$querydat = GRUP.' WHERE id_group="'.$grupid.'"'; 						$content .= '<br/><div id="example1" style="width: 100%; height: 100%; overflow: hidden; font-size=10px;"></div>';						//-------set lebar kolom -------------						$width = "[150,250,100,100,100,100,130,100,100]";						//-------get id pada sql -------------						$field = gen_mysql_id(GRUP);						//-------get header pada sql----------						$name = gen_mysql_head(GRUP);						//-------set header pada handson------						$sethead = "['Group ID','Group','Insert','View','Delete','Edit','Full Controlled','Modified','Remove']";						//-------set id pada handson----------						$setid = "[{data:'ID_Group',className: 'htLeft'},{data:'Group_Name',className: 'htLeft'},{data:'Insert_Data',className: 'htLeft'},{data:'View_Data',className: 'htLeft'},{data:'Delete_Data',className: 'htLeft'},{data:'Edit_Data',className: 'htLeft'},{data:'Full_Control',className: 'htLeft'},{data:'Edit',renderer: 'html'},{data:'Delete',renderer: 'html'}]";						//-------get data pada sql------------						$dt = array($querydat,$field,array('Edit','Delete'),array(PATH_GRUP.EDIT,PATH_GRUP.DELETE),array(),PATH_GRUP);						$data = get_data_handson_func($dt);						$fixedcolleft=0;						$sethandson = array($sethead,$setid,$data,$width,$fixedcolleft);						$content .= get_handson($sethandson);				}			}						//------------Jika ada halaman edit data-------//			if(isset($_REQUEST['edit'])){ $info='';				if(isset($_REQUEST['post'])){											//-- Update data pada kategori aset --//						$field = array(								'group_name',									'insert_data',								'view_data',								'delete_data',								'edit_data',								'full_control');						$value = array(								'"'.$_REQUEST['group'].'"',								'"'.$_REQUEST['insert'].'"',								'"'.$_REQUEST['view'].'"',								'"'.$_REQUEST['deleted'].'"',								'"'.$_REQUEST['edited'].'"',								'"'.$_REQUEST['full'].'"'); 						$query = mysql_stat_update(array('tb_user_group',$field,$value,'id_group="'.$_REQUEST['rowid'].'"')); 						mysql_exe_query(array($query,1)); 						//-- Ambil data baru dari database --//						$querydat = GRUP.' WHERE id_group="'.$_REQUEST['rowid'].'"';						$info .= '<br/><div id="example1" style="width: 1030px; height: 80px; overflow: hidden; font-size=10px;"></div>';						$content .= '<br/><div id="example1" style="width: 100%; height: 100%; overflow: hidden; font-size=10px;"></div>';						//-------set lebar kolom -------------						$width = "[150,250,100,100,100,100,130,100,100]";						//-------get id pada sql -------------						$field = gen_mysql_id(GRUP);						//-------get header pada sql----------						$name = gen_mysql_head(GRUP);						//-------set header pada handson------						$sethead = "['Group ID','Group','Insert','View','Delete','Edit','Full Controlled','Modified','Remove']";						//-------set id pada handson----------						$setid = "[{data:'ID_Group',className: 'htLeft'},{data:'Group_Name',className: 'htLeft'},{data:'Insert_Data',className: 'htLeft'},{data:'View_Data',className: 'htLeft'},{data:'Delete_Data',className: 'htLeft'},{data:'Edit_Data',className: 'htLeft'},{data:'Full_Control',className: 'htLeft'},{data:'Edit',renderer: 'html'},{data:'Delete',renderer: 'html'}]";						//-------get data pada sql------------						$dt = array($querydat,$field,array('Edit','Delete'),array(PATH_GRUP.EDIT,PATH_GRUP.DELETE),array(),PATH_GRUP);						$data = get_data_handson_func($dt);						$fixedcolleft=0;						$sethandson = array($sethead,$setid,$data,$width,$fixedcolleft);						$info .= get_handson($sethandson);									}				//-----Ambil nilai semua data yang terkait dengan id data------//				$querydat = GRUP.' WHERE id_group="'.$_REQUEST['rowid'].'"'; $result=mysql_exe_query(array($querydat,1)); $resultnow=mysql_exe_fetch_array(array($result,1));				$group=$resultnow[1]; $insert=$resultnow[2]; $view=$resultnow[3]; $delete=$resultnow[4]; $edit=$resultnow[5]; $full=$resultnow[6]; 				//-----Tampilan judul pada pengeditan------				$content = '<br/><div class="ade">'.EAGRUP.'</div>';				$content .= '<div class="" align="center"><span><a class="btn btn-outline-primary btn-rounded btn-fw" href="'.PATH_GRUP.'">View</a></span></div>';				//----- Buat Form Isian Berikut-----				$name_field=array('Group','Insert','View','Delete','Edit','Full Controlled');				$input_type=array(							text_je(array('group',$group,'false')),							combo_je_arr(array(array('Yes','No'),'insert','insert',150,'',$insert)),							combo_je_arr(array(array('Yes','No'),'view','view',150,'',$view)),							combo_je_arr(array(array('Yes','No'),'deleted','deleted',150,'',$delete)),							combo_je_arr(array(array('Yes','No'),'edited','edited',150,'',$edit)),							combo_je_arr(array(array('Yes','No'),'full','full',150,'',$full))						);				$signtofill = array('','','','','','');				$content .= create_form(array(FAGRUP,PATH_GRUP.EDIT.'&rowid='.$_REQUEST['rowid'].POST,1,$name_field,$input_type,$signtofill));				$content .= $info;			}						//------------Jika ada halaman delete data-------//			if(isset($_REQUEST['delete'])){				$content = query_delete(array(PATH_GRUP,'tb_user_group','id_group="'.$_REQUEST['rowid'].'"'));			}		return $content;	}?>