<?php	function location(){		$content .= '<br/><div class="ade">'.TLOCATION.'</div>';		$content .= '<div class="toptext" align="center">'._USER_VIEW_._USER_INSERT_.'</div>';		$content .= '<br/><div id="example1" style="width: 100%; height: 100%; overflow: hidden; font-size=10px;"></div>';		//-------set lebar kolom -------------		$width = "[200,800,80]";		//-------get id pada sql -------------		$field = gen_mysql_id(LOCATION);		//-------get header pada sql----------		$name = gen_mysql_head(LOCATION);		//-------set header pada handson------		$sethead = "['ID','Detail Location'"._USER_EDIT_SETHEAD_."]";		//-------set id pada handson----------		$setid = "[{data:'ID',className: 'htLeft'},{data:'Detail_Location',className: 'htLeft'}"._USER_EDIT_SETID_."]";		//-------get data pada sql------------		$dt = array(LOCATION,$field,array('Edit'),array(PATH_LOCATION.EDIT),array(),PATH_LOCATION);		$data = get_data_handson_func($dt);		//----Fungsi memanggil data handsontable melalui javascript---		$fixedcolleft=0;		$sethandson = array($sethead,$setid,$data,$width,$fixedcolleft);		//--------fungsi hanya untuk meload data		if (_VIEW_) $content .= get_handson($sethandson);		//------------Jika ada halaman tambah data-------//		//------------Jika ada halaman tambah data-------//			if(isset($_REQUEST['add'])){				$content = '<br/><div class="ade">'.TLOCATION.'</div>';				$content .= '<div class="toptext" align="center">'._USER_VIEW_._USER_INSERT_.'</div>';				//----- Buat Form Isian Berikut-----				$name_field=array('Location_ID','Detail Location');				$input_type=array(							text_je(array('id','','false')),							text_area_je(array('location','','true'))						);				$signtofill = array('<small id="fill" class="form-text text-muted">Please fill this field.</small>',									'<small id="fill" class="form-text text-muted">Please fill this field.</small>');				$content .= create_form(array('',PATH_LOCATION.ADD.POST,1,$name_field,$input_type,$signtofill));				//------ Aksi ketika post menambahkan data -----//				if(isset($_REQUEST['post'])){					if(!empty($_REQUEST['id']) && !empty($_REQUEST['location'])){						//---------Check ID sudah ada atau tidak--------						$query = 'SELECT COUNT(*) FROM invent_location WHERE id_location="'.$_REQUEST['id'].'"'; 						$result = mysql_exe_query(array($query,1));						$result_now = mysql_exe_fetch_array(array($result,1)); $rows = $result_now[0];						if($rows>0){							$content = empty_info(array('ID Already Exist')).$content;						}else{							//-- Insert data pada kategori aset --//							$field = array(									'id_location',									'detail_location');							$value = array(									'"'.$_REQUEST['id'].'"',									'"'.$_REQUEST['location'].'"'); 							$query = mysql_stat_insert(array('invent_location',$field,$value)); 							mysql_exe_query(array($query,1)); 							//-- Ambil data baru dari database --//							$querydat = LOCATION.' WHERE id_location="'.$_REQUEST['id'].'"'; 							$content .= '<br/><div id="example1" style="width: 100%; height: 100%; overflow: hidden; font-size=10px;"></div>';							//-------set lebar kolom -------------							$width = "[200,800,80]";							//-------get id pada sql -------------							$field = gen_mysql_id(LOCATION);							//-------get header pada sql----------							$name = gen_mysql_head(LOCATION);							//-------set header pada handson------							$sethead = "['ID','Detail Location'"._USER_EDIT_SETHEAD_."]";							//-------set id pada handson----------							$setid = "[{data:'ID',className: 'htLeft'},{data:'Detail_Location',className: 'htLeft'}"._USER_EDIT_SETID_."]";							//-------get data pada sql------------							$dt = array($querydat,$field,array('Edit'),array(PATH_LOCATION.EDIT),array(),PATH_LOCATION);							$data = get_data_handson_func($dt);							$fixedcolleft=0;							$sethandson = array($sethead,$setid,$data,$width,$fixedcolleft);							$content .= get_handson($sethandson);						}					}else{						$content = empty_info(array('Some field is empty')).$content;					}				}			}						//------------Jika ada halaman edit data-------//			if(isset($_REQUEST['edit'])){ $info='';				if(isset($_REQUEST['post'])){					if(!empty($_REQUEST['location'])){						//-- Insert data pada kategori aset --//						$field = array(								'detail_location');						$value = array(								'"'.$_REQUEST['location'].'"'); 						$query = mysql_stat_update(array('invent_location',$field,$value,'id_location="'.$_REQUEST['rowid'].'"')); 						mysql_exe_query(array($query,1)); 						//-- Ambil data baru dari database --//						$querydat = LOCATION.' WHERE id_location="'.$_REQUEST['rowid'].'"'; 						$info .= '<br/><div id="example1" style="width: 100%; height: 100%; overflow: hidden; font-size=10px;"></div>';						//-------set lebar kolom -------------						$width = "[200,800,80]";						//-------get id pada sql -------------						$field = gen_mysql_id(LOCATION);						//-------get header pada sql----------						$name = gen_mysql_head(LOCATION);						//-------set header pada handson------						$sethead = "['ID','Detail Location'"._USER_EDIT_SETHEAD_."]";						//-------set id pada handson----------						$setid = "[{data:'ID',className: 'htLeft'},{data:'Detail_Location',className: 'htLeft'}"._USER_EDIT_SETID_."]";						//-------get data pada sql------------						//-------get data pada sql------------						$dt = array($querydat,$field,array('Edit'),array(PATH_LOCATION.EDIT),array(),PATH_LOCATION);						$data = get_data_handson_func($dt);						$fixedcolleft=0;						$sethandson = array($sethead,$setid,$data,$width,$fixedcolleft);						$info .= get_handson($sethandson);					}else{						$error = empty_info(array('Some field is empty')).$info;					}				}				//-----Ambil nilai semua data yang terkait dengan id data------//				$querydat = LOCATION.' WHERE id_location="'.$_REQUEST['rowid'].'"'; $result=mysql_exe_query(array($querydat,1)); $resultnow=mysql_exe_fetch_array(array($result,1));				$id=$resultnow[0]; $location=$resultnow[1]; 				//-----Tampilan judul pada pengeditan------				$content = '<br/><div class="ade">'.TLOCATION.$id.'</div>';				$content .= '<div class="toptext" align="center"><span><a href="'.PATH_LOCATION.'">View</a></span></div>';				//----- Buat Form Isian Berikut-----				$name_field=array('Location');				$input_type=array(							text_area_je(array('location',$location,'true'))						);				$signtofill = array('<small id="fill" class="form-text text-muted">Please fill this field.</small>');				$content .= create_form(array('',PATH_LOCATION.EDIT.'&rowid='.$_REQUEST['rowid'].POST,1,$name_field,$input_type,$signtofill));				$content = $error.$content.$info;			}		return $content;	}?>